mapper.select:
SELECT id, photo_name, album_id, album_name, photo_description user_id, like_amount, url, creation_time, modification_time, url FROM photo WHERE user_id = ( SELECT id FROM user WHERE username = #{userName}
AND cookie_id = #{cookieId}
)

SELECT id, album_name, photo_description city_id, province_id, visit_amount, like_amount, creation_time, modification_time, url FROM album WHERE id = ( SELECT photo_id FROM photo_like WHERE user_id = ( SELECT id FROM user WHERE username = #{userName}
AND cookie_id = #{cookieId}
) )

mapper.insert:
INSERT INTO photo (photo_name, photo_description, album_name, album_id, user_id, url) VALUES (
    #{photoName}, #{photoDescription}, #{albumName},
    (
        SELECT id
        FROM album
        WHERE album_name = #{albumName}
              AND province_id = (
            SELECT id
            FROM province
            WHERE province_name = #{provinceName}
        )
              AND city_id = (
            SELECT id
            FROM city
            WHERE city_name = #{cityName}
        )
    ),
    (
        SELECT id
        FROM user
        WHERE username = #{userName}
    )
    , #{url})

