WHERE
photo_name = #{photoName} AND
album_name = #{albumName} AND
album_id = (
SELECT id
FROM album
WHERE album_name = #{albumName}
AND province_id = (
SELECT id
FROM province
WHERE province_name = #{provinceName}
)
AND city_id = (
SELECT id
FROM city
WHERE city_name = #{cityName}
)
) AND
user_id = (
SELECT id
FROM USER
WHERE username = #{userName}
)

    AND url = #{url}

