mapper.sql:
SELECT id
FROM city
WHERE city_name = #{cityName}

mapper.insert:
INSERT INTO photo (photo_name, photo_description, album_name, album_id, user_id,username, url) VALUES (
    #{photoName}, #{photoDescription}, #{albumName},
    (
        SELECT id
        FROM album
        WHERE album_name = #{albumName}
              AND province_id = (
            SELECT id
            FROM province
            WHERE province_name = #{provinceName}
        )
              AND city_id = (
            SELECT id
            FROM city
            WHERE city_name = #{cityName}
        )AND
              city_id IS NOT NULL
    ),
    (
        SELECT id
        FROM user
        WHERE username = #{username}
    ),
    #{username},
    #{url})

mapper.delete:
DELETE FROM photo
WHERE
photo_name = #{photoName} AND
album_name = #{albumName} AND
album_id = (
SELECT id
FROM album
WHERE album_name = #{albumName}
AND province_id = (
SELECT id
FROM province
WHERE province_name = #{provinceName}
)
AND city_id = (
SELECT id
FROM city
WHERE city_name = #{cityName}
)
) AND
user_id = (
SELECT id
FROM USER
WHERE username = #{username}
)

    AND url = #{url}

